    private void guardarBActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_guardarBActionPerformed
        try {
            int id = Integer.parseInt(txtId.getText());
            String nombre = txtMateria.getText();
            int anio = Integer.parseInt(txtAnio.getText());
            boolean check = false;
            for (Materia ma : Materia.mat) {
                if (id == ma.getIdMateria()) {
                    check = true;
                }
            }
            if (check == true) {
                JOptionPane.showMessageDialog(null, "El Id ya se encuantra en uso");
                txtId.requestFocus();
            } else {
                Materia mat = new Materia(id, nombre, anio);
                JOptionPane.showMessageDialog(null, "Materia cargada con exito");
            }

        } catch (Exception ex) {
            JOptionPane.showMessageDialog(null, "CODIGO Y AÃ‘O DEBEN CONTENER UN NUMERO ENTERO");
            txtId.requestFocus();
        }

    }//GEN-LAST:event_guardarBActionPerformed


